export default async function getConsultApi() {

    const axios = require('axios');

    const Location = await axios.get('https://geolocation-db.com/json/')

    let date = new Date()
    const Hours = date.getHours() + ':' + date.getMinutes()

    var result = []

    const latitude = await Location.data.latitude
    const longitude = await Location.data.longitude

    await axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=a10534631766659c98a684169d309aa5`)
        .then(async(res) => {
            const data = res.data
            const Temperature = data.main.temp
            const Sensation = data.main.feels_like
            const Humidity = data.main.humidity
            const TemperatureMin = data.main.temp_min
            const TemperatureMax = data.wind.temp_max
            const City = Location.data.city
            const Country = Location.data.country_code

            // Country, City, Hours, Tempe
            result = [Country, City, Hours, Temperature, TemperatureMin, TemperatureMax, Sensation, Wind]
        })

    return result
}
