export default async function getCurrentWeather(location) {

    const axios = require('axios');

    const latitude = location.latitude
    const longitude = location.longitude

    console.log(location)

    var result = []
    console.log(`GET ---> https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=a10534631766659c98a684169d309aa5`)
    axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=a10534631766659c98a684169d309aa5`)
        .then((result) => {
            const data = result.data
            console.log(data)
        })
        .catch((err) => {
            console.log(err)
        });
}
