export default async function getCurrentWeather(location) {

    const axios = require('axios');

    

    var result = []
    console.log(`GET ---> http://history.openweathermap.org/data/2.5/history/city?q=${await location}&appid=a10534631766659c98a684169d309aa5`)
    axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=35&lon=139&appid=a10534631766659c98a684169d309aa5`)
        .then((result) => {
            const data = result.data
            console.log(data)
        })
        .catch((err) => {
            console.log(err)
        });
}
