import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import { useState, useEffect } from 'react';
import axios from 'axios';
// Icons
import { Feather, EvilIcons } from '@expo/vector-icons';
// Components
import MainCard from './components/MainCard';
import InfoCard from './components/InfoCard';
// Api
import getCurrentWeather from './api/ConsultApi';

export default async function App() {

  const [darkMode, setDarkMode] = useState(true)

  const [currentTemperature, setCurrentTemperature] = useState('21')
  const [currentHour, setcurrentHour] = useState('13:59')

  const res = await axios.get('https://geolocation-db.com/json/')
  const [location, setLocation] = useState(res.data)

  // const [locationView, setLocationView] = useState([])

  // const [longitude, setLongitude] = useState([])
  // const [latitude, setLatitude] = useState([])

  // async function getData() {
  //   const res = await axios.get('https://geolocation-db.com/json/')
  //   // setLongitude(res.data.longitude)
  //   // setLatitude(res.data.latitude)
  //   setLocation('a')
  //   // setLocationView(res.data.country_code + ', ' + res.data.city)
  // }

  // const getLocation = async () => {
  //   const res = await axios.get('https://geolocation-db.com/json/')
  //   // setLongitude(res.data.longitude)
  //   // setLatitude(res.data.latitude)
  //   setLocation('a')
  //   // setLocationView(res.data.country_code + ', ' + res.data.city)
  // }

  useEffect(() => {
    getCurrentWeather(location)
  }, [])

  const [Sensation, setSensation] = useState('27.6')
  const [Moisture, setMoisture] = useState('77')
  const [Rain, setRain] = useState('60')
  const [Wind, setWind] = useState('9.4')

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: darkMode ? '#232634' : '#f2f2f2',
      alignItems: 'center',
    },
    temperature: {
      alignItems: 'center',
      flexDirection: 'row',
      marginTop: 10,
    },
    temperatureText: {
      color: darkMode ? '#e0e0e0' : 'black',
      fontSize: 50,
    },
    refreshButton: {
      position: 'absolute',
      margin: 30,
      alignSelf: 'flex-start'
    },
    cardView: {
      color: darkMode ? 'black' : 'white',
      margin: 10,
      flexDirection: 'row',
      alignItems: 'center',
    },
    cardInfo: {
      alignItems: 'center',
      backgroundColor: darkMode ? '#393e54' : '#8f8f8f',
      borderRadius: 20,
      width: 370,
      height: 230,
    },
    cardInfoText: {
      color: darkMode ? '#e0e0e0' : 'white',
      margin: 15,
      fontSize: 20,
      fontWeight: 'bold',
    },
    cardInfoView: {
      flexDirection: 'row',
      flexWrap: 'wrap',
    },
    themeButton: {
      margin: 10,
      marginLeft: 300,
      alignItems: 'center',
      justifyContent: 'center',
      width: 50,
      height: 50,
      borderRadius: 25,
    },
    squareButton: {
      backgroundColor: darkMode ? '#f2f2f2' : '#8f8f8f',
      justifyContent: 'center',
      borderRadius: 20,
      marginRight: 20,
      width: 50,
      height: 25,
    },
    circleButton: {
      backgroundColor: darkMode ? '#232634' : '#f2f2f2',
      alignSelf: darkMode ? 'flex-end' : 'flex-start',
      margin: 5,
      width: 20,
      height: 20,
      borderRadius: 50,
    },
  });

  return (
    <View style={styles.container}>

      <TouchableOpacity style={styles.refreshButton}>
        <EvilIcons name="refresh" size={30} color={darkMode ? 'white' : 'black'} />
      </TouchableOpacity>

      <Feather name="sun" style={{ marginTop: 55 }} size={40} color="orange" />

      <View style={styles.temperature}>
        <Text style={styles.temperatureText}>{currentTemperature}</Text>
        <Text style={[styles.temperatureText, { fontSize: 14 }]}>°C</Text>
      </View>

      <Text style={[styles.temperatureText, { fontSize: 14 }]}>| {currentHour}</Text>

      <View style={styles.cardView}>
        <MainCard title={'Manhã'} temperature={'22'} backgroundColor={darkMode ? '#ff873d' : '#cc6e30'} icon={'morning'}></MainCard>
        <MainCard title={'Tarde'} temperature={'32'} backgroundColor={darkMode ? '#d29600' : '#fcc63f'} icon={'afternoon'}></MainCard>
        <MainCard title={'Noite'} temperature={'27'} backgroundColor={darkMode ? '#008081' : '#38b7b8'} icon={'night'}></MainCard>
      </View>

      <View style={styles.cardInfo}>
        <Text style={styles.cardInfoText}>Informações adicionais</Text>

        <View style={styles.cardInfoView}>
          <InfoCard title={'Sensação'} value={Sensation + '°C'}></InfoCard>
          <InfoCard title={'Umidade'} value={Moisture + '%'}></InfoCard>
          <InfoCard title={'Proba. de chuva'} value={Rain + '%'}></InfoCard>
          <InfoCard title={'Vel. do vento'} value={Wind + 'km/h'}></InfoCard>
        </View>

      </View>

      <View style={styles.themeButton}>
        <View style={styles.squareButton}>
          <TouchableOpacity style={styles.circleButton} onPress={() => darkMode ? setDarkMode(false) : setDarkMode(true)}>

          </TouchableOpacity>
        </View>

      </View>
    </View>
  );
}
