export default async function getConsultApi() {

    const axios = require('axios');

    const Location = await axios.get('https://geolocation-db.com/json/')

    let date = new Date()
    const Hours = date.getHours() + ':' + date.getMinutes()

    var result = []

    const latitude = await Location.data.latitude
    const longitude = await Location.data.longitude

    await axios.get(`http://api.weatherapi.com/v1/forecast.json?key=94feab36963245679cb201951220801&q=${latitude},${longitude}&days=1&api=yes`)
        .then(async(res) => {
            const data = res.data
            console.log(data.forecast.forecastday[0].hour[6].temp_c) // data.forecast.forecastday[0].hour[6] - 06:00 AM
            console.log(data.forecast.forecastday[0].hour[7].temp_c) // 07:00 AM
            console.log(data.forecast.forecastday[0].hour[8].temp_c) // 08:00 AM
            console.log(data.forecast.forecastday[0].hour[9].temp_c) // 09:00 AM
            console.log(data.forecast.forecastday[0].hour[10].temp_c) // 10:00 AM
            console.log(data.forecast.forecastday[0].hour[11].temp_c) // 11:00 AM
            console.log(data.forecast.forecastday[0].hour[12].temp_c) // 12:00 AM -> PM

            var manha1 = data.forecast.forecastday[0].hour[6].temp_c,
            manha2 = data.forecast.forecastday[0].hour[7].temp_c,
            manha3 = data.forecast.forecastday[0].hour[8].temp_c,
            manha4 = data.forecast.forecastday[0].hour[9].temp_c,
            manha5 = data.forecast.forecastday[0].hour[10].temp_c,
            manha6 = data.forecast.forecastday[0].hour[11].temp_c,
            manha7 = data.forecast.forecastday[0].hour[12].temp_c,
            resultado = manha


            const Temperature = res.data.current
            const Sensation = data.feelslike_c
            const Humidity = data.humidity
