export default async function getConsultApi() {

    const axios = require('axios');
    

    async function getLocation() {
        const res = await axios.get('https://geolocation-db.com/json/')
        setLocation(res.data)

        const latitude = await res.data.latitude
        const longitude = await res.data.longitude

        axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=a10534631766659c98a684169d309aa5`)
            .then((result) => {
                const data = result.data
                setTemperature(convertKelvinToC(data.main.temp))
                setSensation(convertKelvinToC(data.main.feels_like))
                setMoisture(data.main.humidity)
                setRain(data.main)
            })
    }
}
