import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import { useState, useEffect } from 'react';
// Icons
import { Feather } from '@expo/vector-icons';

export default function App() {

  const [darkMode, setDarkMode] = useState(true)
  const [currentTemperature, setCurrentTemperature] = useState('21')
  const [location, setLocation] = useState('BR, São Paulo')

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: darkMode ? '#232634' : '000',
      alignItems: 'center',
    },
    temperature: {
      alignItems: 'center',
      flexDirection: 'row',
      marginTop: 10,
    },
    temperatureText: {
      color: darkMode ? '#000' : 'black',
      fontSize: 36,

    }
  });

  return (
    <View style={styles.container}>
      <Feather name="sun" style={{marginTop: 50}} size={36} color="orange" />
      <View style={styles.temperature}>
        <Text style={styles.temperatureText}>{currentTemperature}°C</Text>
      </View>
    </View>
  );
}
