export default async function getCurrentWeather(clientIP) {

    const axios = require('axios');
    const ip = await clientIP

    var result = []
    console.log(`GET ---> https://api.hgbrasil.com/weather?format=json-cors&key=55a51ced&user_ip=${await ip}`)
    axios.get(`https://api.hgbrasil.com/weather?format=json-cors&key=55a51ced&user_ip=${ip}`).then((result) => {
        const data = result.data
        console.log(data)
    }).catch((err) => {
        console.log(err)
    });
}
